var getVariableFromRaw = function (input, variable) {
    var finalInput;
    var cutChar;
    var name = '"' + variable + '":';
    var index = input.indexOf(name);
    //console.log("index " + index);
    var newInput = input.substring(index + name.length);
    //console.log("newInput " + newInput + "name.length " + name.length);
    if(newInput.indexOf('"') === 0){
        finalInput = newInput.substring(1);
        //console.log("finalInput " + finalInput);
    }else{
        finalInput = newInput;
        cutChar = true;
    }
    var value = finalInput.substring(0, finalInput.indexOf('"'));
    if (cutChar){
        value = value.substring(0, value.length - 1);
    }
    //console.log("value " + value);
    return value;
};
exports.getVariableFromRaw = getVariableFromRaw;

